def getFoodItemsLinks(food_item_url):
    """
        Function to Process on the One Food item link Given in the food_table_link and Get all the links of the Items Describe in different 
        categories defined in that food category. To process further....
    """
    tmp_list = []
    session = requests.Session()
    retry = Retry(connect=3, backoff_factor=0.5)
    adapter = HTTPAdapter(max_retries=retry)
    session.mount('http://', adapter)
    session.mount('https://', adapter)

    session.get(url)
    food_source = session.get(url+food_item_url)

    food_soup = BeautifulSoup(food_source.content, 'html.parser')
    for i in food_soup.findAll('div',{'class':'food_links'}):
        for x in i.findAll('a',{'href':True}):
            tmp_list.append(x['href'])
    logging.info("SUCCESS: All foods in a specific category fetched successfully")
    return tmp_list

