def itemData(item_url):
    tmp_dict = dict()
    session = requests.Session()
    retry = Retry(connect=3, backoff_factor=0.5)
    adapter = HTTPAdapter(max_retries=retry)
    session.mount('http://', adapter)
    session.mount('https://', adapter)

    session.get(url)
    item_source = session.get(url+item_url)

    item_soup = BeautifulSoup(item_source.content, 'html.parser')
    try: 
        item_name = item_soup.findAll('h1')[0].text 
    except: 
        item_name =  str(item_url) + '-tmp_name'
    tmp_value_list = list() # (Cals, Fat, Carbs, Prot)
    for i in item_soup.findAll('div',{'class':'factValue'}):   
        tmp_value_list.append(i.text)
    tmp_dict[item_name] = tmp_value_list
    print("Data fetched  and return successfully for: ", item_name)
    
    logging.info("SUCCESS: Data for a item fetched successfully")
    return tmp_dict

